    const [formData, setFormData] = useState({});

    const handleChange = (e) => {
        const { name, value } = e.target;
        console.log(value , 'val')
        setFormData((prevData) => ({
            ...prevData,
            [name]: value,
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        console.log('hi')
        console.log('Form submitted:', formData);
        try {
            // setIsLoading(true)
