            console.log(error)
            // setEmailReapeatError(error?.response?.data?.email)
            // setIsLoading(false)
            // setPhoneNumberError(error?.response?.data?.phone_number)
        }
    };

    return (
        <>
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="lastName">text:</label>
                    <input
                        type="text"
                        name={"text"}
                        value={formData.text}
                        defaultValue={formData.text}
                        onChange={handleChange}
                    />
                </div>
                <div>
                    <label htmlFor="email">column:</label>
                    <select id="column" name="column" value={formData?.column} onChange={handleChange} >
                        <option value="inProgress" >inProgress</option>
                        <option value="todo" >todo</option>
                    </select>
                </div>
                <button type="submit">Submit</button>
            </form>
        </>
    )
}

export default AddTaskForm
