import axios from 'axios';
import React, { useState } from 'react'

function AddTaskForm() {
    var id = '01';
    var text = '02';
    var column = '03';
    const [formData, setFormData] = useState({});

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData((prevData) => ({
            ...prevData,
            [name]: value,
        }));
    };

    const handleSubmit = async(e) => {
        e.preventDefault();
        console.log('Form submitted:', formData);
        try {
            setIsLoading(true)
            // make POST request using Axios
            await axios.post(`https://64c1fab4fa35860baea1054d.mockapi.io/roles/add-task/add-task`, formData);

            // handle successful response
            // if (response.status === 201) {
            //   setIsLoading(false)
            //   var signUpData = response?.data?.signup
            //   dispatch(setQulificationStore({ signUpData })) // id direct store in redux
            //   dispatch(setQulificationStore({ basicInformation })) // basic information store in redux
            //   navigate('/plan')
            // }
          } catch (error) {
            console.log(error)
            // setEmailReapeatError(error?.response?.data?.email)
            // setIsLoading(false)
            // setPhoneNumberError(error?.response?.data?.phone_number)
          }
    };

    return (
        <>
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="firstName">id:</label>
                    <input
                        type="text"
                        id="01"
                        name={"id"}
                        value={formData.id}
                        defaultValue={formData.id}
                        onChange={handleChange}
                    />
                </div>
                <div>
                    <label htmlFor="lastName">text:</label>
                    <input
                        type="text"
                        id="02"
                        name={"text"}
                        value={formData.text}
                        defaultValue={formData.text}
                        onChange={handleChange}
                    />
                </div>
                <div>
                    <label htmlFor="email">column:</label>
                    <input
                        type="text"
                        id="03"
                        name={"column"}
                        value={formData.column}
                        defaultValue={formData.column}
                        onChange={handleChange}
                    />
                </div>
                <button type="submit">Submit</button>
            </form>
        </>
    )
}

export default AddTaskForm
