import React, { useEffect, useState } from 'react';
import { makeStyles } from '@mui/styles';
import axios from 'axios';

const useStyles = makeStyles({
    root: {
        width: '150px',
        height: '50px',
        backgroundColor: 'blue',
        color: '#fff',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        cursor: 'pointer', // Add cursor pointer for better UX
        marginBottom: '5px' // Add margin bottom for spacing between tasks
    }
});
const KanBanBoard = () => {
    const classes = useStyles();
    const [error, setError] = useState()
    const [response, setResponse] = useState()
    useEffect(() => {
        axios.get(`https://64c1fab4fa35860baea1054d.mockapi.io/roles/add-task`)
            .then((response) => setResponse(response))
            .catch((error) => setError(error?.response?.data?.detail))
    }, [])

    console.log(response , 'res')
    const [tasks, setTasks] = useState([
        { id: 1, text: 'Task 1', column: 'todo' },
        { id: 2, text: 'Task 2', column: 'todo' },
        { id: 3, text: 'Task 3', column: 'inProgress' },
        { id: 4, text: 'Task 4', column: 'inProgress' },
    ]);

    const handleDragStart = (event, id) => {
        console.log(tasks, 'start')
        event.dataTransfer.setData('text/plain', id);
    };

    const handleDrop = (event, column) => {
        console.log(tasks, 'drop')
        const taskId = event.dataTransfer.getData('text/plain');
        const updatedTasks = tasks.map((task) => {
            if (task.id === parseInt(taskId, 10)) {
                return { ...task, column };
            }
            return task;
        });
        setTasks(updatedTasks);
    };

    return (
        <div style={{ display: 'flex' }}>
            <div
                style={{ border: '1px solid #ccc', padding: '10px', marginRight: '10px' }}
                onDrop={(e) => handleDrop(e, 'todo')}
                onDragOver={(e) => e.preventDefault()}
            >
                <h2>Todo</h2>
                {tasks
                    .filter((task) => task.column === 'todo')
                    .map((task) => (
                        <div
                            key={task.id}
                            draggable
                            className={classes.root}

                            onDragStart={(e) => handleDragStart(e, task.id)}
                            style={{ border: '1px solid #ddd', padding: '8px', marginBottom: '8px' }}
                        >
                            {task.text}
                        </div>
                    ))}
            </div>

            <div
                style={{ border: '1px solid #ccc', padding: '10px' }}
                onDrop={(e) => handleDrop(e, 'inProgress')}
                onDragOver={(e) => e.preventDefault()}
            >
                <h2>In Progress</h2>
                {tasks
                    .filter((task) => task.column === 'inProgress')
                    .map((task) => (
                        <div
                            key={task.id}
                            draggable
                            className={classes.root}

                            onDragStart={(e) => handleDragStart(e, task.id)}
                            style={{ border: '1px solid #ddd', padding: '8px', marginBottom: '8px' }}
                        >
                            {task.text}
                        </div>
                    ))}
            </div>
        </div>
    );
};

export default KanBanBoard;
